#include<bits/stdc++.h>
using namespace std;
class Solution {
    public:
    int numSteps(string s) {
        int steps=0;
        while (s!="1") {
            if (s.back()=='0') {
                s.pop_back();
            }   
            else {
                int length=s.length()-1;
                for (int i=length;i>=0;i--) {
                    if (s[i]=='0') {
                        s[i]='1';
                        break;
                    }
                    s[i]='0';
                    if (i==0) {
                        s='1'+s;
                    }
                }
            }
            steps++;
        }
        return steps;
    }
};
int main(){
    string s="";
    Solution obj;
    cout<<s.length()<<endl;
    cout<<obj.numSteps(s)<<endl;
    return 0;
}